mapper.select:
SELECT a.*, b.*, c.role_id, c.role_name
FROM z_user a
LEFT JOIN z_user_role b ON a.user_id = b.user_id
LEFT JOIN z_role c ON b.role_id = c.role_id


        a.user_name LIKE CONCAT('%', #{userName}, '%')


        a.name LIKE CONCAT('%', #{name}, '%')


        a.status = #{status}


        a.user_id NOT IN (

            #{id}

        )


LIMIT #{offset},#{limit}

SELECT COUNT(a.user_id)
FROM z_user a


        a.user_name LIKE CONCAT('%', #{userName}, '%')


        a.name LIKE CONCAT('%', #{name}, '%')


        a.status = #{status}


        a.user_id NOT IN (

            #{id}

        )
