mapper.select:
SELECT a.*, b.*, c.role_id, c.role_name, d.org_name
FROM z_user a
LEFT JOIN z_user_role b ON a.user_id = b.user_id
LEFT JOIN z_role c ON b.role_id = c.role_id
LEFT JOIN z_organization d ON a.org_id=d.org_id
WHERE 1=1



            /*包含下级*/
            AND d.org_path LIKE CONCAT('%', #{orgId}, '%')


            AND a.org_id = #{orgId}




    AND a.user_name LIKE CONCAT('%', #{userName}, '%')


    AND a.name LIKE CONCAT('%', #{name}, '%')


    AND a.status = #{status}


    AND a.user_id NOT IN

        #{id}


LIMIT #{offset},#{limit}

SELECT COUNT(a.user_id)
FROM z_user a
LEFT JOIN z_organization d ON a.org_id=d.org_id
WHERE 1=1



            /*包含下级*/
            AND d.org_path LIKE CONCAT('%', #{orgId}, '%')


            AND a.org_id = #{orgId}




    AND a.user_name LIKE CONCAT('%', #{userName}, '%')


    AND a.name LIKE CONCAT('%', #{name}, '%')


    AND a.status = #{status}


    AND a.user_id NOT IN

        #{id}
